## https://old.reddit.com/r/dailyprogrammer/comments/8g0iil/20180430_challenge_359_easy_regular_paperfold/

def fold(n):
    x = "10"
    res = "1"
    for _ in range(1, n):
        t = ""
        for i, c in enumerate(res):
            t += x[i % 2] + c
            if i == (len(res) - 1):
                t += x[(i + 1) % 2]
        res = t
    return res

assert fold(1) == "1"
assert fold(2) == "110"
assert fold(3) == "1101100"
assert fold(4) == "110110011100100"
assert fold(9) == ""
